(1, 'wc-refund-returns-page', 'info', 'en_US', 'Setup a Refund and Returns Policy page to boost your store\'s credibility.', 'We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.', '{}', 'unactioned', 'woocommerce-core', '2021-10-07 18:07:35', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-07 18:07:35', NULL, 0, 'plain', '', 0, 'info'),
(3, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Sign up for tips, product updates, and inspiration', 'We\'re here for you - get tips, product updates and inspiration straight to your email box', '{}', 'unactioned', 'woocommerce-admin', '2021-10-07 18:07:35', NULL, 0, 'plain', '', 0, 'info'),
(4, 'wc-admin-set-up-additional-payment-types', 'info', 'en_US', 'Set up additional payment providers', 'Set up additional payment providers, using third-party services or other methods.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-07 18:13:48', NULL, 0, 'plain', '', 0, 'info'),
(5, 'wc-payments-notes-set-up-refund-policy', 'info', 'en_US', 'Set up refund policy', 'Protect your merchant account from fraudulent disputes by defining the policy and making it accessible to customers.', '{}', 'unactioned', 'woocommerce-payments', '2021-10-07 18:14:11', NULL, 0, 'plain', '', 0, 'info'),
(6, 'wc-payments-notes-set-https-for-checkout', 'info', 'en_US', 'Force secure checkout', 'Protect your customers data and increase trustworthiness of your store by forcing HTTPS on checkout pages.', '{}', 'unactioned', 'woocommerce-payments', '2021-10-07 18:14:11', NULL, 0, 'plain', '', 0, 'info'),
(7, 'wc-payments-notes-multi-currency-available', 'info', 'en_US', 'Sell worldwide in multiple currencies', 'Boost your international sales by allowing your customers to shop and pay in their local currency.', '{}', 'unactioned', 'woocommerce-payments', '2021-10-07 18:14:11', NULL, 0, 'plain', '', 0, 'info'),
(8, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-07 18:18:26', NULL, 0, 'plain', '', 0, 'info'),
(9, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-10 11:06:56', NULL, 0, 'plain', '', 0, 'info'),
(10, 'wc-admin-woocommerce-subscriptions', 'marketing', 'en_US', 'Do you need more info about WooCommerce Subscriptions?', 'WooCommerce Subscriptions allows you to introduce a variety of subscriptions for physical or virtual products and services. Create product-of-the-month clubs, weekly service subscriptions or even yearly software billing packages. Add sign-up fees, offer free trials, or set expiration periods.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-10 11:06:56', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-10 11:06:56', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2021-10-10 11:06:56', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-10 11:06:56', NULL, 0, 'plain', '', 0, 'info');
